package Day23_Arrays_Started;

public class PrintNumbersWithLoops2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int j = 1; j <= 10; j++) {
			for (int i = 1; i >=j; i++) {
				System.out.print(i + " ");
			}
			System.out.println();
	
	
	}

		// TODO Auto-generated method stub
		for (int j = 10; j >= 1; j--) {
			for (int i = j; i >=1; i--) {
				System.out.print(i + " ");
			}
			System.out.println();
	}
	}
}
